 import sys

 def put_mines_last_step(R, C, M, grid):
 	if M == 0:
 		return
 	R -= 1
 	C -= 1
 	grid[R][C] = '*'
 	M -= 1
 	r = R - 1
 	c = C - 1
 	while M > 0:
 		if r > c:
 			grid[r][C] = '*'
 			r -= 1
 		else:
 			grid[R][c] = '*'
 			c -= 1
 		M -= 1

 def put_mines(R, C, M, grid):
 	if R > C:
 		if M < C:
 			put_mines_last_step(R, C, M, grid)
 			return
 		for i in range(C):
 			grid[R - 1][i] = '*'
 		put_mines(R - 1, C, M - C, grid)
 		return
 	if M < R:
 		put_mines_last_step(R, C, M, grid)
 		return
 	for i in range(R):
 		grid[i][C - 1] = '*'
 	put_mines(R, C - 1, M - R, grid)
 	return

