 if len(sys.argv) > 1:
     r = open(sys.argv[1], 'r')

 total_cases = r.readline()
 for case_number in range(1, int(total_cases) + 1):
     values = map(float, r.readline().split(' '))
     solve_case(values[0], values[1], values[2], case_number)

