def solve(before, beforeSel, after, afterSel):
	candidates = set(before[beforeSel]) & set(after[afterSel])

	if len(candidates) == 0:
		return "Volunteer cheated!"
	elif len(candidates) == 1:
		return candidates.pop()
	else:
		return "Bad magician!"

