def min_time(C, F, X, speed, nb_cookies):
	if (X - nb_cookies) / speed < (X - (nb_cookies - C)) / (speed + F):
		return (X - nb_cookies) / speed
	elif nb_cookies >= C:
		return min_time(C, F, X, speed + F, nb_cookies - C)
	else:
		return (C - nb_cookies) / speed + min_time(C, F, X, speed, C)

