def solve(num_surprises, p, ts):
    s = 0
    u = 0
    for t in ts:
        a = int(t/3)
        r = t % 3
        if a+1 >= p and not r == 0:
            u += 1
        elif a >= p and r == 0:
            u += 1
        elif a > 0 and a+1 >= p and r == 0:
            s += 1
