def isPal(n):
    l = str(n)
    return l == l[::-1]

def solve(a, b):
    l = int(math.ceil(math.sqrt(a)))
    u = int(math.floor(math.sqrt(b)))

    count = 0
    for x in range(l, u + 1):
        if isPal(x):
            if isPal(x*x):
