import os

class Solver(object):
    def __init__(self):
        pass

    def solve(self, inputs):
        R, C, M = [int(x) for x in inputs[0].split()]
        mp = []
        for r in range(R):
            mp.append(['.']*C)
        mp[0][0] = 'c'
        outputs = []
        if M == 0:
            for row in mp:
                outputs.append(''.join(row))
            return outputs
        rr, cc, rm = R, C, M
        while rm >= min(rr, cc):
            if rr <= cc:
                for r in range(rr):
                    mp[r][cc-1] = '*'
                cc -= 1
                rm -= rr
            else:
                for c in range(cc):
                    mp[rr-1][c] = '*'
                rr -= 1
                rm -= cc

        if rm == 0:
            if (min(rr, cc), max(rr, cc)) == (1, 2) and min(R, C) != 1:
                return ['Impossible']
        else:
            if min(rr, cc) - rm >= 2:
                if rr <= cc:
                    for r in range(rr-rm, rr):
                        mp[r][cc-1] = '*'
                else:
                    for c in range(cc-rm, cc):
                        mp[rr-1][c] = '*'
            else:
                if min(rr, cc) >= 4:
                    if rr <= cc:
                        for r in range(2, rr):
