def up_down(board, height, y, x):
    # Move up from (x, y).
    above = y - 1
    while above >= 0:
        if board[above][x] > board[y][x]: 
            return False
        above -= 1
    # Move down from (x, y).
    below = y + 1
    while below < height:
        if board[below][x] > board[y][x]: 
            return False
        below += 1
    return True

def left_right(board, width, y, x):
    # Move left from (x, y).
    before = x - 1
    while before >= 0:
        if board[y][before] > board[y][x]: 
            return False
        before -= 1
    # Move right from (x, y).
    after = y + 1
    while after < width:
        if board[y][after] > board[y][x]: 
            return False
        after += 1
    return True

