import sys


def compute(C, F, X):
    k = int(math.floor(X / C - 2.0 / F))
    if k <= 0:
        return X / 2
    total = 0.0
    for i in xrange(k):
        total += 1.0 / (2.0 + i * F)
    return C * total + X / (2.0 + k * F)


