import sys

def solve(N, M, grid):
    possible = [[False for _ in xrange(M)] for _ in xrange(N)]
    for i in xrange(N):
        m = max(grid[i])
        for j in xrange(M):
            possible[i][j] = possible[i][j] or grid[i][j] == m

    for j in xrange(M):
        m = max(grid[_][j] for _ in xrange(N))
        for i in xrange(N):
            possible[i][j] = possible[i][j] or grid[i][j] == m

    if all(all(row) for row in possible):
        return "YES"
    else:
        return "NO"

