def conj_mat(a):
	R = len(a)
	C = len(a[0])
	res = [['.' for _ in xrange(R)] for __ in xrange(C)]
	for i in xrange(R):
		for j in xrange(C):
			res[j][i] = a[i][j]
	return res

def one_line_builder(R, C, M):
	res = []
	res.extend(['*'] * M)
	res.extend(['.'] * (C - M))
	res[-1] = 'c'
	return [res]

def two_line_builder(R, C, M):
	line = []
	line.extend(['*'] * (M / 2))
	line.extend(['.'] * (C - M / 2))
	res = [line, copy_list(line)]
	res[1][-1] = 'c'
	if M%2 == 1:
		res[0][-1] = '*'
	return res

def three_line_builder(R, C, M):
	res = [['.' for _ in xrange(C)] for __ in xrange(R)]
	res[-1][-1] = 'c'
	m = min([M, R * C - 9])
	stop_flag = False
	for j in xrange(C):
