			res[0][j] = '*'
		else:
			res[0][-1] = '*'

	if M <= R * C - 9:
		return res
	else:
		m = M - (R * C - 9)
		assert m not in [2, 4, 6, 7, 9]
		assert m > 0
		assert m < 10

		res[-3][-3] = '*'
		m -= 1
		if m == 0: return res
		res[-2][-3] = '*'
		res[-1][-3] = '*'
		m -= 2
		if m == 0: return res
		res[-3][-2] = '*'
		res[-3][-1] = '*'
		m -= 2
		if m == 0: return res
		res[-2][-2] = '*'
		res[-2][-1] = '*'
		res[-1][-2] = '*'
		m -= 3
		if m == 0: return res
		assert False



def over_three_line_builder(R, C, M):
	if M <= (R - 3) * C:
		res = [['*' for _ in xrange(C)] for __ in xrange(M / C)]
		flag = False
		if (M % C) != (C - 1):
			line = ['*' for _ in xrange(M % C)]
			line.extend(['.' for _ in xrange(C - (M % C))])
		else:
			line = ['*' for _ in xrange((M % C) - 1)]
			line.extend(['.' for _ in xrange((C - (M % C)) + 1)])
			flag = True
		res.append(line)
		index = len(res)
		res.extend([['.' for _ in xrange(C)] for __ in xrange(R - len(res))])
		if flag:
			res[index][0] = '*'
		res[-1][-1] = 'c'
		assert len(res) == R
		assert len(res[0]) == C
		return res
	else:
		res = [['*' for _ in xrange(C)] for __ in xrange(R - 3)]
		M -= (R - 3) * C
		tmp = three_line_builder(3, C, M)
		if len(tmp) != 3: # error msg
			return tmp
		res.extend(tmp)
		return res

def solve(R, C, M):
	res = None

	if M == R * C:
		return 'Impossible'
	if R >= 3 and C >= 3:
		if R*C - M in [7, 5, 3, 2]:
			return 'Impossible'
		return over_three_line_builder(R, C, M)

	elif R != 1 and C != 1:  #which means one of them is 2, and the other not 1
		if (R*C - M) % 2 == 1:
			if M < R*C - 1:
				return 'Impossible'
		if M + 2 == R * C:
			return 'Impossible'
		else:
			if R == 2:
				return two_line_builder(R, C, M)
			else:
				tmp = two_line_builder(C, R, M)
				return conj_mat(tmp)

	else:					#which means one of them is 1
		if R == 1:
			return one_line_builder(R, C, M)
		else:
			tmp = one_line_builder(C, R, M)
			return conj_mat(tmp)

	return res

