		line = game[i]
		c = line.count(symbol)
		if c == 4 or (c == 3 and "T" in line):
			won = True
		i += 1
	return won

def transpose(game):
	for i in xrange(3):
		for j in xrange(i + 1, 4):
			aux = game[i][j]
			game[i][j] = game[j][i]
			game[j][i] = aux
	return game

def testColumns(symbol, game):
	game = transpose(game)
	return testLines(symbol, game)

def testDiagonals(symbol, game):
	won1 = True
	won2 = True
	i = 0
	while i < 4 and (won1 or won2):
		if game[i][i] not in [symbol, "T"]:
			won1 = False
		if game[i][3 - i] not in [symbol, "T"]:
			won2 = False
		i += 1
	return (won1 or won2)		

