def perfsq(n):
    sq = int(math.sqrt(n))
    if n == sq * sq:
        return sq
    return 0

def palindrome(n):
    s = str(n)
    return (s == s[::-1])

def getpal(n):
    if n == 1:
        for i in range(10):
            yield i
    else:
        n2 = n / 2
        for x in xrange(10 ** (n2 - 1), 10 ** n2):
            s = str(x)
            if n % 2:
                for i in range(10):
                    ns = s + str(i) + s[::-1]
                    yield int(ns)
            else:
                ns = s + s[::-1]
                yield int(ns)

