	# if it got this far it must be right
	return (True, c)

def checkCol(L, col):
	c = L[0][col];

	# making sure it isnt an empty line
	if (c == '.'):
		return (False, None)

	for i in range(1,4):
		if not (L[i][col] == c or L[i][col] == 'T'):
