	for t in range(1, n + 1):
		L = []
		# loading game
		for i in range(4):
			L.append(f.readline().strip());

		# checking game state
		for i in range(4):
			#checking row
			result = checkRow(L[i])
			if result[0]:
				out.write("Case #" + str(t) + ": " + result[1] + " won
")
				break
			#checking column
			result = checkCol(L,i)
			if result[0]:
				out.write("Case #" + str(t) + ": " + result[1] + " won
")
				break
		else:
			#checking column
			result = checkDiag(L)
			if result[0]:
				out.write("Case #" + str(t) + ": " + result[1] + " won
")
			else:
				if checkComplete(L):
					out.write("Case #" + str(t) + ": Draw
")
				else:
					out.write("Case #" + str(t) + ": Game has not completed
")

		f.readline() #skipping empty line

	#closing files
