def passes(p, t): #normal, surprising
    q, r = divmod(t, 3)
    if r == 0:
        return q >= p, q + 1 >= p and q
    elif r == 1:
        return q + 1 >= p, q + 1 >= p and q
    elif r == 2:
        return q + 1 >= p, q + 2 >= p

