def trans(A):
    if not A:
        return None
    B = get_a(len(A[0]), len(A))
    for i, a in enumerate(A):
        for j, v in enumerate(a):
            B[j][i] = v
    return B


def check(A, m, r, c):
    if A is None:
        return True
    cnts = {'c': 0, '*': 0, '.': 0}
    #print(draw(A))
    #print(assert len(A) == r)
    for i in xrange(r):
        assert len(A[i]) == c
        for j in xrange(c):
            cnts[A[i][j]] += 1
    #print(m, r, c, "=>", cnts)
