def solve(m, r, c):
    if r > c:
        return trans(solve(m, c, r))
    assert r <= c
    assert m != r * c
    e = r * c - m
    # we click always in the right bottom corner
    if e == 1:
        A = get_a(r, c, '*')
        return A
    if r == 1:
        A = get_a(1, c, '.')
        for i in xrange(m):
            A[0][i] = '*'
        return A
    if r == 2:
        if e == 2 or e % 2 == 1:
            return None
        A = get_a(2, c, '.')
        assert m % 2 == 0
        for i in xrange(m / 2):
            A[0][i] = A[1][i] = '*'
        return A
    assert r >= 3
    A = get_a(r, c, '*')
    if e in (2, 3, 5, 7):
        return None
    E = [c] * (e / c) + ([e % c] if e % c else [])
    if sum(E) < e:
        E.append(e % c)
        assert sum(E) == e
    if len(E) == 1:
        E = [e / 2] * 2
        if sum(E) < e:
            e.append(1)
    if E[0] != E[1]:
        s = sum(E[:2])
        E[0] = E[1] = s / 2
        if sum(E[:2]) != s:
            assert len(E) == 2
            E.append(1)
    if E[-1] == 1:
        if len(E) > 3:
            E[-2] -= 1
            E[-1] += 1
        else:
            E[0] -= 1
            E[1] -= 1
            E[2] += 2
    for i in xrange(len(E)):
        for j in xrange(E[i]):
            A[-i - 1][-j - 1] = '.'
    return A


