import sys
import itertools


def solve(C, F, X):
    def needtime(cookies, farm):
        speed = 2.0 + F * farm
        return cookies / speed

    farm = 0
    pasttime = 0
    while True:
        complete = needtime(X, farm)
        nextfarm = needtime(C, farm)
        nextchallenge = needtime(X, farm + 1)
        if complete <= nextfarm + nextchallenge:
            return pasttime + complete
        pasttime += nextfarm
        farm += 1


