import sys
import math


def ispalindrome(n):
    return str(n) == str(n)[::-1]


def solve(A, B):
    solution = []
    for n in range(int(math.sqrt(A)), int(math.sqrt(B))+1):
        if ispalindrome(n):
            m = n ** 2
            if ispalindrome(m) and A <= m <= B:
                solution.append(n)
    return len(solution)


