def atos(A):
    return '
'.join([''.join(row) for row in A])


def get_field(r, c):
    field = [['.'] * c for i in range(r)]
    field[0][0] = 'c'
    return field


def is_forbidden(i, j):
    return i == 0 and j == 0 or i == 0 and j == 1 or \
        i == 1 and j == 0 or i == 1 and j == 1


def fill(field, r, c, m):
    left = m
    for ii in range(r - 1, -1, -1):
        i = ii
        j = c - 1
        while i < r and j >= 0:
            if is_forbidden(i, j):
                i += 1
                j -= 1
                continue
            if left == 0:
                return 0
            field[i][j] = '*'
            left -= 1

            i += 1
            j -= 1

        if ii == 0:
            for jj in range(c - 2, 1, -1):
                i = ii
                j = jj
                while i < r and j >= 0:
                    if is_forbidden(i, j):
                        i += 1
                        j -= 1
                        continue
                    if left == 0:
                        return 0
                    field[i][j] = '*'
                    left -= 1

                    i += 1
                    j -= 1

    if r * c == m + 1:
        if r > 1:
            field[1][0] = '*'
        if c > 1:
            field[0][1] = '*'
        if r > 1 and c > 1:
            field[1][1] = '*'
        return 0

    return left


