def solve_problem(first, first_rows, second, second_rows):
    intersection = set(first_rows[first - 1]) & set(second_rows[second - 1])

    if not intersection:
        return "Volunteer cheated!"
    elif len(intersection) > 1:
        return "Bad magician!"
    else:
        return intersection.pop()



