        elif O == 4:
            ans = 'O won'

    # vertical
    for col in range(4):
        X, O = 0, 0
        for row in range(4):
            spot = lines[row][col]
            if spot == 'O':
                O += 1
            elif spot == 'X':
                X += 1
            if spot == 'T':
                O += 1
                X += 1
        if X == 4:
            ans = 'X won'
        elif O == 4:
            ans = 'O won'

    # diagonal
    X1, X2, O1, O2 = 0, 0, 0, 0
    for i in range(4):
        spot1 = lines[i][i]
        spot2 = lines[4-i-1][i]

        if spot1 == 'O':
            O1 += 1
        elif spot1 == 'X':
            X1 += 1
        elif spot1 == 'T':
            O1 += 1
            X1 += 1

        if spot2 == 'O':
            O2 += 1
        elif spot2 == 'X':
            X2 += 1
        elif spot2 == 'T':
            O2 += 1
            X2 += 1

    if X1 == 4 or X2 == 4:
        ans = 'X won'
    elif O1 == 4 or O2 == 4:
        ans = 'O won'


    print('Case #%(T)s: %(ans)s' % locals())

# -*- coding: utf-8 -*-

import sys
import copy

def next_pos(grass, goal, elems):
    while elems:
        val = elems[0]
        for n in range(len(goal)):
            for m in range(len(goal[0])):
                if goal[n][m] == val and goal[n][m] < grass[n][m]:
                    return (n, m)
        elems.remove(val)
    return None

def check(grass, goal):
    possible = True
    completed = True
    for n in range(len(grass)):
        for m in range(len(grass[0])):
            if grass[n][m] > goal[n][m]:
                completed = False
            elif grass[n][m] < goal[n][m]:
                possible = False

    return possible, completed

def cut(grass, goal, pos, h, elems):

    def _cut(grass, goal, pos, h, is_horizontal):
        if is_horizontal:
            for m in range(len(grass[0])):
                grass[pos[0]][m] = h
        else:
            for n in range(len(grass)):
                grass[n][pos[1]] = h
        possible, completed = check(grass, goal)
        if completed and possible:
            return 'YES'
        elif not completed and possible:
            pos = next_pos(grass, goal, elems)
            if not pos:
                return 'NO'
            h = goal[pos[0]][pos[1]]
            return cut(grass, goal, pos, h, elems)
        elif not possible:
            return 'NO'

    ans1 = _cut(copy.deepcopy(grass), goal, pos, h, is_horizontal=True)
    if ans1 == 'YES':
        return 'YES'
    ans2 = _cut(copy.deepcopy(grass), goal, pos, h, is_horizontal=False)
    if ans2 == 'YES':
        return 'YES'
    return 'NO'


def calc(grass, goal):
    def _elems(goal):
        elems = set()
        for n in range(len(goal)):
            for m in range(len(goal[0])):
                elems.add(goal[n][m])
        return sorted(list(elems), reverse=True)

    elems = _elems(goal)
    pos = next_pos(grass, goal, elems)
    h = goal[pos[0]][pos[1]]
    return cut(grass, goal, pos, h, elems)


N = int(sys.stdin.readline())
for T in range(1, N+1):
    N, M = map(int, sys.stdin.readline().strip().split())

    goal = []
