    outcome = 'YES'
    for s in s_list:
        if matrix[s[1]][s[2]] == 0:
            continue

        row, col = s[1], s[2]

        can_do = True
        for i in range(N):
            can_do &= matrix[i][col] <= s[0]
        if can_do:
            for i in range(N):
                matrix[i][col] = 0
            continue

        can_do = True
        for j in range(M):
            can_do &= matrix[row][j] <= s[0]
        if can_do:
            for j in range(M):
                matrix[row][j] = 0
        else:
            outcome = 'NO'
            break

    print('Case #%d: %s' % (t, outcome,))


