IMPOSSIBLE = set([(2,2,2), (2,2,1), (2,3,1), (2,4,1), (2,5,1)])

for i in range(2,51):
    IMPOSSIBLE.add((2,i,1))
    IMPOSSIBLE.add((i,2,1))

SOLN = {
    (1,2,1) : ["c*"],
    (2,1,1) : ["c", "*"],
    (2,2,3) : ["c*", "**"],
}

def solve(R, C, M):
    if M == 0:
        s = ["c%s" % ('.' * (C-1))]
        for i in range(R-1):
            s.append('.' * C)
        return s
    t = (R, C, M)
    if t in IMPOSSIBLE:
        debug("** %s %s %s" % t)
        raise ValueError
    if t in SOLN:
        return SOLN[t]

    # last row?
    if C < M and R > 2:
        try:
            return solve(R-1, C, M-C) + ["*" * C]
        except ValueError:
            pass

    if C <= R:
        if M >= C and R > 2:
            return solve(R-1, C, M-C) + ["*" * C]
    else:
        if M >= R and C > 2:
            return ["%s*" % s for s in solve(R, C-1, M-R)]
    if R > 2:
        # fill in last row
        mines = min(C, M)
        if mines == C - 1:
            mines -= 1
        try:
            return solve(R-1, C, M-mines) + [("." * (C - mines)) + ("*" * mines)]
        except ValueError:
            if C > 2:
                mines = min(R, M)
            if mines == R - 1:
                mines -= 1
            s = ["%s%s" % (s, '*' if k > C-mines else '.') for k, s in enumerate(solve(R, C-1, M-mines))]
            return s
    debug(R, C, M)
    return []

