import sys


def check(R, C, M, _board):
    # deepcopy
    board = [line[:] for line in _board]

    pos = [(0, 0)]
    while pos:
        row, col = pos.pop()
        # neighbor cell list
        neighbor = []
        for r in (-1, 0, 1):
            r += row
            for c in (-1, 0, 1):
                c += col
                if r >= 0 and r < R and c >= 0 and c < C:
                    neighbor.append((r, c))
        # count bomb
        count = len([1 for r, c in neighbor if board[r][c] == '*â€‹'])
        board[row][col] = str(count)
        # push next cell
        if count == 0:
            for r, c in neighbor:
                if board[r][c] == '.â€‹':
                    pos.append((r, c))

    flat = ''â€‹â€‹.join(''â€‹â€‹.join(line) for line in board)
    result = not flat.count('.â€‹')
    if not result and False: # for DEBUG
        print('-â€‹' * 20)
        print(R, C, M)
        print('
'.â€‹join(''â€‹â€‹.join(line) for line in _board))
        print('-â€‹' * 20)
    assert flat.count('*â€‹') == M
    return result


def solve(R, C, M):
    # initialize
    board = [['.â€‹'] * C for row in range(R)]
    board[0][0] = 'câ€‹'
    row = R
    col = C
    mine = M

    # phase 1: right edge, bottom edge
    while mine:
        if 0 < row <= col and mine >= row:
            for r in range(row):
                board[row - r - 1][col - 1] = '*â€‹'
            mine -= row
            col -= 1
        elif 0 < col <= row and mine >= col:
            for c in range(col):
                board[row - 1][col - c - 1] = '*â€‹'
            mine -= col
            row -= 1
        else:
            break

    # phase 2:
    if mine:
        #print('
'.â€‹join(''â€‹â€‹.join(line) for line in board))
        #print('lâ€‹eft', mine)
        while mine and row > 2:
            for r in range(min(mine, row - 2)):
                board[row - r - 1][col - 1] = '*â€‹'
                mine -= 1
            col -= 1
        while mine and col > 2:
            for c in range(min(mine, col - 2)):
                board[row - 1][col - c - 1] = '*â€‹'
                mine -= 1
            row -= 1

    # phase 3
    if mine:
        # col == row == 2
        if mine:
            board[1][1] = '*â€‹'
            mine -= 1
        if mine:
            board[1][0] = '*â€‹'
            mine -= 1
        if mine:
            board[0][1] = '*â€‹'
            mine -= 1

    assert mine == 0
    return '
'.â€‹join(''â€‹â€‹.join(line) for line in board) if check(R, C, M, board) else 'Impossible'


